describe '#get_open_spots' do
  it 'should take an array and return the number of groups of 5 * characters on each line' do
    expect(get_open_spots([["X","X","*","X","X","*","*","X","*"],["X","X","X"]])).to eq(0)
    expect(get_open_spots([["X","X","*","X","X","*","*","X","*","*"],["X","X","*"]])).to eq(1)
    expect(get_open_spots([["*","X","*","X","X","*","*","X","*"],["*","X","*","X","X","*","*","X","*"]])).to eq(2)
    expect(get_open_spots([["X","X","*","X","X","*","*","X","*","*"],["X","X","*","X","X","*","*","X","*","*"]])).to eq(2)
  end
end